let friendAjaxHandler=function(e,t,r){$.ajax({type:t,url:e,success:function(e){r.remove(),successNotification(e)},error:function(e){errorNotification(e.responseJSON,"Internal Server Error!")}})};$("main").on("click",".action-button",(function(e){"I"==e.target.tagName&&(e.target=e.target.parentElement);let t=e.target.getAttribute("data-user-id"),r=e.target.getAttribute("id");console.log(r);let n=e.target.parentElement.parentElement.parentElement;"remove"==r&&friendAjaxHandler(`/friend-request/remove-friend/${t}`,"delete",n)}));