$(".materialboxed").materialbox();let friendAjaxHandler=function(e,t,n){$.ajax({type:t,url:e,success:function(e){$(".action-buttons").empty(),$(".action-buttons").append(n),successNotification(e)},error:function(e){errorNotification(e.responseJSON,"Internal Server Error!")}})},friendAjaxPostHandler=function(e,t,n){$.ajax({type:"post",url:e,data:t,success:function(e){$(".action-buttons").empty(),$(".action-buttons").append(n),successNotification(e)},error:function(e){errorNotification(e.responseJSON,"Internal Server Error!")}})};$("main").on("click",".action-button",(function(e){"I"==e.target.tagName&&(e.target=e.target.parentElement);let t=e.target.getAttribute("data-user-id"),n=e.target.getAttribute("id");if(console.log(n),"connect"==n){friendAjaxPostHandler("/friend-request/send",{friendId:t},`\n            <div id = "withdraw" class = "action-button" data-user-id = "${t}">\n                Withdraw Request\n            </div>\n        `)}else if("accept"==n){friendAjaxHandler(`/friend-request/accept/${t}`,"put",`\n            <div id = "remove" class = "action-button" data-user-id = "${t}">\n                Remove Friend <i class="fas fa-user-minus"></i>\n            </div>\n        `)}else if("reject"==n){friendAjaxHandler(`/friend-request/reject/${t}`,"put",`\n            <div id = "connect" class = "action-button" data-user-id = "${t}">\n                Connect <i class="fas fa-plus"></i>\n            </div>\n        `)}else if("remove"==n){friendAjaxHandler(`/friend-request/remove-friend/${t}`,"delete",`\n            <div id = "connect" class = "action-button" data-user-id = "${t}">\n                Connect <i class="fas fa-plus"></i>\n            </div>\n        `)}else if("withdraw"==n){friendAjaxHandler(`/friend-request/withdraw/${t}`,"put",`\n            <div id = "connect" class = "action-button" data-user-id = "${t}">\n                Connect <i class="fas fa-plus"></i>\n            </div>\n        `)}}));